import React from 'react';
import { Link, Outlet } from 'react-router-dom';
import BandDisplayAll from './BandDisplayAll';
import { motion } from 'framer-motion';


function Bands() {
    return(
        <div className='container'>
            <div id="bandNav" className="flex justify-center">
                <Link to="/band/new" className=" text-white hover:bg-emerald-500 py-1 px-2 rounded-full mx-1">Create</Link>
                <Link to="/band/:id" className="bg-blue-600 text-white py-1 px-2 rounded-full mx-1">Read</Link>
                <Link to="/band/edit/:id" className="bg-red-500 text-white py-1 px-2 rounded-full mx-1">Edit</Link>
            </div>
            <Outlet />
            <BandDisplayAll />
        </div>
    );
};

export default Bands;
