import React from 'react';
import BookShelf from './BookShelf';
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa';


const Landing = () => {
    return (
        <div className="px-6 px-5">
            <body className="justify-center mt-10 max-w-7xl sm:mt-12 sm:px-6 md:mt-16 lg:mt-20 lg:px-8 xl:mt-28">
                <h2>Choose Your Story!</h2>
                <FaArrowLeft size={40}/>
                <div id="slider" className="w-full h-full container mx-auto px-auto sm:text-center lg:text-left flex overflow-x-scroll overflow-x-auto scroll whitespace-nowrap scroll-smooth cursor-pointer hover:scale-105 ease-in-out duration-300">
                    <BookShelf />
                </div>
            </body>
            <br/>
        </div>
    );
};

export default Landing;
