import React,{ useState }  from 'react';

function Card({prop}) {
    
    const [ editable, setEditable ] = useState(false);

    const handleClick = () => {
        setEditable(!editable);
    };

    return (
        <div className="flex justify-center text-3x1 bg-slate-300 md:p-10 md:w-60 md:h-60 md:bg-slate-200 items-center drop-shadow-md rounded-md">
            <div className={editable ? "opacity-0" : "opacity-800"}>{prop}</div>
            <div className="text-sm cursor-pointer text-grey-500">{editable ? "close | update" : "edit"}</div>

            <input className="text-sm w-15 bg-slate-100" type={!editable && "hidden"} value={prop}/>
        </div>
    );
};

export default Card;
