import React from 'react';
import Book1 from "./OpeningBooks/Book1";
import Book2 from "./OpeningBooks/Book2";
import Book3 from "./OpeningBooks/Book3";
import Book4 from "./OpeningBooks/Book4";
import Book5 from "./OpeningBooks/Book5";
import Book6 from "./OpeningBooks/Book6";
import Book7 from "./OpeningBooks/Book7";
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa';


const Landing = () => {

    const slideLeft = () => {
        var slider = document.getElementById('slider');
        slider.scrollLeft = slider.scrollLeft - 500;
    };

    const slideRight = () => {
        var slider = document.getElementById('slider');
        slider.scrollLeft = slider.scrollLeft + 500;
    };

    return (
        <div className="px-6 px-5 w-full ">
            <body className="justify-center mt-10 max-w-7xl sm:mt-12 sm:px-6 md:mt-16 lg:mt-20 lg:px-8 xl:mt-28">
                <h2>Choose Your Story!</h2>
                <div className="relative flex items-center">
                    <FaArrowLeft onClick={slideLeft} className="opacity-50 cursor-pointer hover:opacity-100" size={30} />
                    <div id="slider" className="h-full w-full mx-auto px-auto sm:text-center lg:text-left flex overflow-x-scroll overflow-x-auto scroll whitespace-nowrap scroll-smooth cursor-pointer hover:scale-105 ease-in-out duration-300">
                        <Book1/>
                        <Book2/>
                        <Book3/>
                        <Book4/>
                        <Book5/>
                        <Book6/>
                        <Book7/>
                    </div>
                    <FaArrowRight onClick={slideRight} size={30} />
                </div>
            </body>
            <br/>
        </div>
    );
};

export default Landing;
