// DISPLAY ALL
import React, { useEffect, useState } from 'react';
import { useNavigate } from "react-router-dom";
import axios from "axios";

const BandDisplayAll = () => {

    const navigate = useNavigate();
    const [allBandLibs, setAllBandLibs] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:8000/api/band')
        .then((res) => {
            console.log(res.data);
            setAllBandLibs(res.data);
        })
        .catch((err) => {
            console.log(err);
        });
    }, []);

    const deleteFilter = (idFromBelow) => {
        axios.delete(`http://localhost:8000/api/band/${idFromBelow}`)
        .then((res) => {
            console.log("Successful delete");
            console.log(res.data);
            const filteredBandLibs = allBandLibs.filter((bandLib) => {
                return bandLib._id !== idFromBelow;
            });
            setAllBandLibs(filteredBandLibs);
        })
        .catch((err) => {
            console.log("An error has occured when attempting to delete the Mad Lib");
            console.log(err.res);
        });
    };

    return (
        
        <div className="container">
            <div>Mad Libs</div>
            <div>
                { allBandLibs.map((bandLib, index) => (
                    <div key={index}>
                        <div>
                            <p>By: {bandLib.yourName}</p>
                            <p>
                                I don't come from your average {bandLib.adjective} family. That's because we're all 
                                part of the {bandLib.name} Family rock band and we lead a very {bandLib.adjective2} 
                                life...
                            </p>
                            <div className="actions">
                                <button onClick={ () => navigate(`/band/${bandLib._id}`) }>
                                    Expand
                                </button>
                                <button onClick={ () => navigate(`/band/edit/${bandLib._id}`) }>
                                    Edit
                                </button>
                                <button onClick={ () => deleteFilter(bandLib._id) }>
                                    Delete
                                </button>
                            </div>
                        </div>
                        <hr />
                    </div>
                ))}
            </div>
        </div>
    );
};

export default BandDisplayAll;
