import React from 'react';
import BookShelf from './BookShelf';
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa';


const Landing = () => {

    const slideLeft = () => {
        var slider = document.getElementById('slider');
        slider.scrollLeft = slider.scrollLeft - 500;
    };

    const slideRight = () => {
        var slider = document.getElementById('slider');
        slider.scrollLeft = slider.scrollLeft + 500;
    };

    return (
        <div className="px-6 px-5">
            <body className="justify-center min-h-7x1 max-w-7xl sm:mt-12 sm:px-6 md:mt-16 lg:mt-20 lg:px-8 xl:mt-28">
                <h2>Choose Your Story!</h2>
                <div className="flex items-center">
                    <FaArrowLeft onClick={slideLeft} className="opacity-50 cursor-pointer hover:opacity-100" size={30} />
                    <div id="slider" className="sm:text-center lg:text-left">
                        <BookShelf />
                    </div>
                    <FaArrowRight onClick={slideRight} className="opacity-50 cursor-pointer hover:opacity-100" size={30} />
                </div>
            </body>
            <br/>
        </div>
    );
};

export default Landing;
