import React, { useState } from 'react';
import Card from './Card';
import { initialState } from './BookImport';

function Carousel() {

    const [cards, setCards] = useState[initialState];
    
    const handleLeftClick = (isLeft) => {
        const prevState = [...cards];
        const firstCurrentActiveCardIdx = prevState
        .filter((ft) => ft.active === true)
        .sort((a, b) => (a.pos > b.pos ? 1 : b.pos > a.pos ? -1 : 0))[0].idx;
        var nextInactiveCardIdx;
        if(prevState.filter((ft) => ft.active === true))
        prevState.find((f) => f.active === false).active = true;
        prevState.find((f) => f.idx === nextCardIdx).active = false;
        prevState.find((f) => f.idx === nextCardIdx).pos =
            Math.max.apply(
                null,
                prevState.map(function (o) {
                    return o.pos;
                })
            ) + 1;
        setCards(prevState);
    };

    const handleRightClick = () => {
        const prevState = [...cards];
        const nextCardIdx = prevState
        .filter((ft) => ft.active === true)
        .sort((a, b) => (a.pos > b.pos ? 1 : b.pos > a.pos ? -1 : 0))
        .pop(1).idx;
        prevState.find((f) => f.active === false).pos =
            Math.min.apply(
                null,
                prevState.map(function (o) {
                    return o.pos;
                })
            ) - 1;
        prevState.find((f) => f.idx === false).active = true;
        prevState.find((f) => f.idx === nextCardIdx).active = false;
        setCards(prevState);
    };

    return (
        <>
            <div
            className="text-xl md:text-5x1 cursor-pointer"
            onClick={() => handleLeftClick()}
            >
                {"<"}
            </div>
            {cards
            .filter((f) => f.active === true)
            .sort((a, b) => (a.pos > b.pos ? 1 : b.pos > a.pos ? -1 : 0))
            .map((card, index) => {
                <Card key={index} prop={card.text} />
            })}
            <div
            className="text-xl md:text-5x1 cursor-pointer"
            onClick={() => handleRightClick()}
            >
                {">"}
            </div>
        </>
    );
};

export default Carousel;
